     <!-- Your widget code goes here --><form>Fahrenheit:<input name="F" value="" size="6" maxlength="6" onchange="eval('C.value = ' + this.form.C_expr.value);eval('K.value = ' + this.form.K_expr.value)" type="text">  <br>  <input name="F_expr" value="(Math.round(((212-32)/100 * C.value + 32)*100))/100;" type="hidden"><input name="F_expr2" value="(Math.round(((212-32)/100 *(K.value - 273) + 32)*100))/100; " type="hidden"> &nbsp; &nbsp; &nbsp;Celsius:<input name="C" value="" size="6" maxlength="6" onchange="eval('F.value = ' + this.form.F_expr.value);eval('K.value = ' + this.form.K_expr.value)" type="text">  <br>  <input name="C_expr" value="(Math.round((100/(212-32) * (F.value - 32))*100))/100" type="hidden"> <input name="C_expr2" value="(Math.round(K.value - 273))" type="hidden">&nbsp;&nbsp; &nbsp; &nbsp; Kelvin:<input name="K" value="" size="6" maxlength="6" onchange="eval('F.value = ' + this.form.F_expr2.value);eval('C.value = ' + this.form.C_expr2.value)" type="text">  <br>  <input name="K_expr" value="(Math.round((100/(212-32) * (F.value - 32))*100))/100 + 273" type="hidden">&nbsp;  <br>  <input name="Reset" value="Submit" type="button"><input name="Reset" value="Reset" type="reset">  <p></p></form>
           